import React from "react";
import Avatar from "boring-avatars";
import TextareaAutosize from "react-textarea-autosize";
import { useContext } from "react";
import { observer } from "mobx-react";
import StoresContext from "../util/context";

interface Itextarea {
  textarea: JSX.Element;
}

const PostCard = observer(function PostCard(): JSX.Element {
  const store = useContext(StoresContext);

  observer(function txtarea() {
    return (
      <TextareaAutosize
        className={"flex-grow resize-none"}
        placeholder={"What are your thoughts?"}
        onChange={(e) => {
          store.refStore.postInputHandler(e);
        }}
      >
        {store.refStore.formattedSelection}
      </TextareaAutosize>
    );
  });
  return (
    <div className="flex items-start mb-5 justify-left">
      <div className="self-start mt-0">
        <Avatar
          size={40}
          name="Maria Mitchell"
          variant="marble"
          colors={["#92A1C6", "#146A7C", "#F0AB3D", "#C271B4", "#C20D90"]}
        />
      </div>
      <div className={"flex flex-col flex-grow pl-3 text-sm"}>
        <div>
          <p className={" font-extralight"}>
            commentBoi879<span className="ml-3"></span>
          </p>
        </div>
        <p className={"flex "}></p>
        <div className={"inline"}>
          {/*up button*/}
          <button>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="inline w-4 h-4"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={1}
                d="M5 15l7-7 7 7"
              />
            </svg>
          </button>
          {/*down button*/}
          <button>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="inline w-4 h-4"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={1}
                d="M19 9l-7 7-7-7"
              />
            </svg>
          </button>
          <div className={" inline font-extralight"}>
            <span className={"ml-6"}>reply</span>
            <span className={"ml-6"}>post</span>
            <span className={"ml-6"}>save</span>
            <span className={"ml-6"}>hide</span>
            <span className={"ml-6"}>history</span>

            <span className={"ml-6"}>127d</span>
          </div>
        </div>
      </div>
    </div>
  );
});

export default PostCard;
