import React, {JSXElementConstructor} from 'react'
import bible from '../data/kjv.json'

type AppProps = {
  chapter: string;
};


const Bible = () => {

  const textHandler =  (text : Array<{verse: string, text: string}>) => {
    return text.map(t =>{
      return(
        <span>{" "}
        <sup>{JSON.stringify(t.verse).replace(/^"|"$/g, '')}</sup>
          <span>{JSON.stringify(t.text).replace(/^"|"$/g, '')}</span>
        </span>
        )
    })
}

  return (
    <div>
    <h1 className={"align-center text-5xl py-7 font-bold"}>{JSON.stringify(bible.book.toUpperCase()).replace(/^"|"$/g, '')}</h1>
      <div className=" w-5/6 m-auto ">
      <h2 className={"text-7xl float-left inline px-3"}>{JSON.stringify(bible.chapters[0].chapter).replace(/^"|"$/g, '')}</h2>
        {textHandler(bible.chapters[0].verses)}

      </div>
    </div>
  )
  }

  export default Bible
