import { makeAutoObservable, toJS } from "mobx";

interface IRefStore {}

export default class RefStore {
  constructor() {
    makeAutoObservable(this);
  }

  //selected verse
  yellowVerse: any[] = [];

  postInput = "";

  logVerse(e: any) {
    console.log("RefStore", "logVerse", e);
  }
  // push or remove verse from selected verse
  yellowVerseHandler(book: string, chapter: string, verse: string) {
    const obj = { book: book, chapter: chapter, verse: verse };
    const index = this.yellowVerse.findIndex(
      (item) =>
        item.book === obj.book &&
        item.chapter === obj.chapter &&
        item.verse === obj.verse
    );
    this.yellowVerse.some(
      (item) =>
        item.book === obj.book &&
        item.chapter === obj.chapter &&
        item.verse === obj.verse
    )
      ? this.yellowVerse.splice(index, 1)
      : this.yellowVerse.push(obj);
    //this.yellowVerse.push({ book: book, chapter: chapter, verse: verse });
    console.log("RefStore", "yellowVerseHandler", toJS(this.yellowVerse));
  }

  postInputHandler(input: string) {
    this.postInput = input;
  }
}
