import { useContext } from "react";
import {observer} from "mobx-react";
import Bible from "./Bible";
import Comments from "./Comments";
import StoresContext from "../util/context";


const Nav = observer(
function Nav () {
  const store = useContext(StoresContext)

  return (
    <>
      {/*Bible container*/}
      <div className={store.navStore.navPosition === 'bottom'? 'h-full' : 'h-navmain'}>
        <Bible/>
      </div>
      {/* Nav container /*}
      <div className={"flex justify-center flex-initial"}>
        <button onClick={() => {store.navStore.handleNavPosition('top')}}>
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 inline" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 15l7-7 7 7" />
          </svg>
        </button>
        <button onClick={() => {store.navStore.handleNavPosition('bottom')}}>
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 inline" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
          </svg>
        </button>
      </div>
      {/*Comments container*/}
      <div className='mb-1 overflow-auto'>
        <Comments/>
      </div>
      </>
  )
})

export default Nav
